import React from 'react';
import { SimpleForm, ImageInput, ImageField } from 'react-admin';
import { Box } from '@mui/material';
import './ImageInputForm.css'


const ImageInputForm = () => {
    return (
        <div className='f4 pa2 w-70 '>
            <SimpleForm>
            <Box sx={{ bgcolor: '#e8f4f8', padding: 2, borderRadius: 2 }}>
                    <ImageInput 
                        source="images"  // Set a valid source for storing images
                        accept="image/*" // Correct accept syntax
                        multiple={false} // Correctly set multiple prop as boolean
                        placeholder={<p>Drop your face image here!</p>}
                        sx={{ bgcolor: '#f0f8ff', padding: 2, borderRadius: 2 }}
                    >
                        <ImageField source="src" title="title" sx={{ bgcolor: '#cceeff', padding: 1 }} />
                    </ImageInput>
                </Box>
            </SimpleForm>
        </div>

    )
}

export default ImageInputForm;
